import { Form, Button } from "react-bootstrap"
import { EmployeeContext } from "../context/EmployeeContext";
import { useContext, useState } from "react";

const EditForm = (selectEmployee) => {

    const id = selectEmployee.id;

    const [name, setName] = useState(selectEmployee.name);
    const [email, setEmail] = useState(selectEmployee.email);
    const [address, setAddress] = useState(selectEmployee.address);
    const [phone, setPhone] = useState(selectEmployee.phone);

    const {updateEmployee} = useContext(EmployeeContext);


     return (

        <Form>
            <Form.Group>
                <Form.Control
                    type="text"
                    placeholder="Name *"
                    name="name"
                    value={name}
                    required
                />
            </Form.Group>
            <Form.Group>
                <Form.Control
                    type="email"
                    placeholder="Email *"
                    name="email"
                    value={email}
                    required
                />
            </Form.Group>
            <Form.Group>
                <Form.Control
                    as="textarea"
                    placeholder="Address"
                    rows={3}
                    name="address"
                    value={address}
                />
            </Form.Group>
            <Form.Group>
                <Form.Control
                    type="text"
                    placeholder="Phone"
                    name="phone"
                    value={phone}
                />
            </Form.Group>
            <Button variant="success" type="submit" block>
                Edit Employee
            </Button>
        </Form>

     )
}

export default EditForm;
