import {useEffect, useState} from "react";

const Pagination = () =>{

    const pages = 5;

    const numOfPages = [];

    for (let i = 1; i <= pages; i++) {
        numOfPages.push(i);
    }

    // initial state is 1 because you start at first page of pagination
    const [currentBtn, setCurrentBtn] = useState(1);
    return(
        <div class="clearfix">
                <div className="hint-text">Showing <b>5</b> out of <b>25</b> entries</div>
				<ul className="pagination">
                    <li className="`${currentBtn === 1 ? 'page-item disabled' : 'page-item'}`"><a href="#!"
                        onClick={ () => setCurrentBtn((prev) => prev === 1 ? prev : prev - 1)}
                    >Previous</a></li>

                   { 
                        numOfPages.map((page, index) => {
                            return (
                                <li key={index} className="`${currentBtn === page ? 'page-item active' : 'page-item'}`"><a href="#!"></a></li>
                            )
                        })
                    }

                    <li className="`${currentBtn === numOfPages.length ? 'page-item disabled' : 'page-item'}`"><a href="#!"
                        onClick={ () => setCurrentBtn((next) => next === numOfPages.length ? next : next + 1)}
                    >Next</a></li>      
				</ul>
        </div>

    )
}

export default Pagination;

